/* Reset */

* {
  margin: 0px;
  padding: 0px;
  box-sizing: border-box;
  scroll-behavior: smooth;
}

/* Main style */
//Colors
$color-background: #FBF7F4;
$color-main-dark: #0F6565;
$color-secondary-light: #C4F8E6;
$color-secondary-dark: #0F6565;
$color-tertiary: #B64F2E;

//Media-queries
$media-query1: 1000px;
$media-query2: 550px;

//Common
%margin-tiles {
  margin-top: 50px;
  @media (max-width: $media-query1) and (min-width: $media-query2) {
    margin-top: 40px;
  }
  @media (max-width: $media-query2) {
    margin-top: 25px;
  }
}

%margin-tiles-line {
  margin-top: 170px;
  @media (max-width: $media-query1) and (min-width: $media-query2) {
    margin-top: 85px;
  }
  @media (max-width: $media-query2) {
    margin-top: 50px;
  }
}

$margin-screen: 225px;
$margin-tablet: 125px;
$margin-phone: 75px;

$border-box: 1px solid $color-main-dark;
$text-shadow: 3px 3px 0px $color-secondary-light;

//Font

$font-header: "Montserrat", sans-serif;
$font-body: "Quicksand", sans-serif;

%h1 {
  color: $color-main-dark;
  font: 500 35px $font-header;
  @media (max-width: $media-query2) {
    font: 500 27px $font-header;
  }
}

%h2 {
  color: $color-main-dark;
  font: 500 35px $font-header;
  @media (max-width: $media-query2) {
    font: 500 27px $font-header;
  }
}

%h3 {
  color: $color-main-dark;
  font: 500 28px $font-header;
  @media (max-width: $media-query2) {
    font: 500 23px $font-header;
  }
}

%h4 {
  color: $color-main-dark;
  font: 500 22px $font-header;
  @media (max-width: $media-query2) {
    font: 500 19px $font-header;
  }
}

%p {
  color: $color-main-dark;
  font: 300 18px $font-body;
  @media (max-width: $media-query2) {
    font: 300 16px $font-body;
  }
}

%font-navbar {
  color: $color-main-dark;
  font: 500 22px $font-header;
  @media (max-width: $media-query2) {
    font: 500 18px $font-header;
  }
}

%font-p {
  color: $color-main-dark;
  font: 300 18px $font-body;
  @media (max-width: $media-query2) {
    font: 300 16px $font-body;
  }
}

%font-pbold {
  color: $color-main-dark;
  font: 500 18px $font-body;
  @media (max-width: $media-query2) {
    font: 500 16px $font-body;
  }
}

%font-small {
  color: $color-main-dark;
  font: 300 14px $font-body;
  @media (max-width: $media-query2) {
    font: 300 12px $font-body;
  }
}

$font-handwriting: 400 30px "Caveat", cursive;

body {
  width: 100%;
  background-image: radial-gradient($color-secondary-light 7%, $color-background 7%);
  background-attachment: fixed;
  background-position: 0 0;
  background-size: 20px 20px;

  @media (max-width: $media-query2) {
    background-size: 15px 15px;
    background-image: radial-gradient($color-secondary-light 10%, $color-background 10%);
  }
}

a,
a:visited {
  text-decoration: none;
  color: $color-main-dark;
}

ul {
  list-style-type: none;
}

h1 {
  @extend %h1;
}

h2 {
  @extend %h2;
}

h3 {
  @extend %h3;
}

h4 {
  @extend %h4;
}

p {
  @extend %p;
}

/* Header */

header {
  width: 100%;
  height: 100px;
  padding-left: 2%;
  padding-right: 2%;
  display: flex;
  align-items: center;
  z-index: 5;
  justify-content: center;

  @media (max-width: $media-query2) {
    position: relative;
    padding-left: 0;
    padding-right: 0;
  }
}

.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;

  @media (max-width: $media-query2) {
    flex-wrap: wrap;
    justify-content: center;
  }
}

/* Header - Logo */

.navbar .logo {
  height: 60px;
  cursor: pointer;
  transition: all 0.5s ease-in-out;
 text-shadow: $text-shadow;
  @media (max-width: $media-query2) {
    margin-top: 5px;
    height: 25px;
    margin-bottom: 5px;
    margin-right: 10px;
  }
}

.navbar .logo:hover {
  height: 50px;
  cursor: pointer;

  @media (max-width: $media-query2) {
    height: 30px;
  }
}

/* Header - Menu */

.navbar .menu {
  display: flex;

  @media (max-width: $media-query2) {
    justify-content: space-between;
  }
}

ul.menu a {
  cursor: pointer;
  height: 100%;
  display: flex;
  align-items: center;
  padding: 10px 20px;
  margin-left: 2.5vw;
  color: $color-main-dark;
  @extend %font-navbar;
  border: 1px solid $color-main-dark;
  position: relative;
  transform: translateZ(0);
  transition: all 1200ms;
  background-color: $color-background;

  @media (max-width: $media-query1) and (min-width: $media-query2) {
    padding: 10px 12px;
  }
  @media (max-width: $media-query2) {
    padding: 10px 2vw;
    margin-left: 2vw;
  }
}

ul.menu a::before{
  content: "";
  position: absolute;
  z-index: -1;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: $color-secondary-light;
  transform: scaleX(0);
  transform-origin: 0 50%;
  transition-property: transform;
  transition: 500ms ease-in;
}

ul.menu a:hover::before{
  transform: scaleX(1);
}

ul.menu li:first-child a {
  margin-left: 0px;
}

ul.menu li.current a {
  text-shadow: $text-shadow;
}


/* Landing  */

section.landing {
  display: flex;
  justify-content: center;
  align-items: center;
  height: calc(100vh - 100px);
  width: 100%;

  @media (max-width: $media-query1) {
    margin-top: 0px;
    height: 90vh;
    width: 100vw;
    padding: 0;
  }

  @media (max-width: $media-query2) {
    margin-top: 0px;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
    width: 100vw;
    padding: 0px;
  }
}

/* Footer */

footer {
  display: flex;
  flex-direction: column;
  height: 100%;
  background-color: $color-background;
  border-top: 1px solid $color-secondary-dark;
  align-items: center;
  margin-top: $margin-screen;
  padding-bottom: 30px;

  @media (max-width: $media-query1) {
    margin-top: $margin-tablet;
  }
  @media (max-width: $media-query2) {
    margin-top: $margin-phone;
  }
}

footer::before{
  content: "";
  border-top: solid 10px transparent;
  width:100%;
  display: block;
  z-index: 2;
  background-color: $color-secondary-light;
  position:relative;
  top:-20px;

}

.footer-text {
  display: flex;
  justify-content: space-between;
  width: 100%;
  height: 100%;
  position:relative;

  @media (max-width: $media-query1) {
    justify-content: flex-start;
  }

  @media (max-width: $media-query2) {
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    height: fit-content;
    outline: none;
  }
}

.footer-container {
  display: flex;
  flex-direction: column;
  padding-top: 15px;
  padding-left: 2vw;
  width: 47vw;
  @media (max-width: $media-query1) {
    padding-left: none;
    width:35vw;
  }
  @media (max-width: $media-query2) {
    align-items: center;
    padding-left: none;
  }
}

footer .footer-tile {
  padding: 10px;

  @media (max-width: $media-query2) {
    display: flex;
    flex-direction: column;
    align-items: center;
  }
}

footer .footer-tile p:last-child {
  margin-top: 10px;
  @extend %font-pbold;
 
}

footer a {
  @extend %font-pbold;
}

/* Footer - thanks */

footer .thanks {
  writing-mode: vertical-rl;
  transform: rotate(180deg);
  margin-right: 1vw;
  align-self: center;
  width: 3vw;

  @media (max-width: $media-query1) {
    right:5px;
    position:absolute;
    }
  @media (max-width: $media-query2) {
    position:static;
    writing-mode: horizontal;
    transform: rotate(-90deg);
    margin-right: 0vh;
    width: fit-content;
    justify-content: flex-end;
    align-self: none;
    height: fit-content;
  }
}

footer .thanks p {
  font: 300 18px "Caveat", cursive;
  @media (max-width: $media-query2) {
  }
}

footer .thanks .favourite {
  display: inline-block;
  transform: scale(1, 0, 7);
  font-size: 1rem;
  overflow: hidden;
}

/* Landing - main page - email */

section.landing .email {
  writing-mode: vertical-lr;
  transform: rotate(180deg);
  position: absolute;
  left: 2vh;

  @media (max-width: $media-query2) {
    display: none;
  }
}



/* Landing - main page -welcome-container */

.welcome-container {
