    outline-offset: 10px;
    padding: 10px;
    padding-top: 20px;
    justify-content: center;
    max-width: 275px;
    max-height: 275px;
  }
}

.anim {opacity: 0;}

@keyframes anim1 {
  from {
    transform: translateY(-100px);
    opacity: 0;
  }
  to  {
    transform: translateY(0);
    opacity: 1;
  }
}

.welcome-box .hello {
  font: 400 50px "Caveat", cursive;
  margin-bottom: 50px;
  //text-shadow:3px 3px 0px #F6B8A0;

  @media (max-width: $media-query2) {
    margin-bottom: 20px;
  }
}

.welcome-container p.signature {
  margin-top: 40px;
  font: $font-handwriting;

  @media (max-width: $media-query2) {
    margin-top: 10px;
  }
}

/* Animation - Hello */

.aboutme > p {
  white-space: nowrap;
  overflow: hidden;
}

.aboutme p:nth-child(1) {
  animation: typing 1.5s steps(30, end), blink-caret 0.5s 4;
  animation-fill-mode: forwards;
}

.aboutme p:nth-child(2) {
  opacity: 0;
  animation: typing 1.5s steps(30, end), blink-caret 0.5s 4;
  animation-delay: 1.5s;
  animation-fill-mode: forwards;
}

.aboutme p:nth-child(3) {
  opacity: 0;
  animation: typing 1.5s steps(40, end), blink-caret 0.5s 4;
  animation-delay: 3s;
  animation-fill-mode: forwards;
}

.aboutme p:nth-child(4) {
  opacity: 0;
  animation: typing 1.5s steps(40, end), blink-caret 0.5s 4;
  animation-delay: 4.5s;
  animation-fill-mode: forwards;
}

@keyframes typing {
  from {
    width: 0;
    opacity: 0;
  }
  1% {
    opacity: 1;
  }
  to {
    width: 100%;
    opacity: 1;
  }
}

/* The typewriter cursor effect */

@keyframes blink-caret {
  from,
  to {
    border-color: transparent;
  }
  50% {
    border-right: 0.3em solid $color-main-dark;
  }
  100% {
    border: none;
  }
}

/* Banner */

.banner {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.banner::before,
.banner::after {
  content: "";
  height: 1px;
  width: 100%;
  background-color: $color-main-dark;
}

.banner h2,h1 {
  margin: 0 6vw;
  width: 600px;
  white-space: nowrap;
  text-align: center;
}

/* Projects - main page */

section.projects {
  display: flex;
  flex-direction: column;
  justify-content: center;
  max-width: 1300px;
  margin-right: auto;
  margin-left: auto;
  padding-right: 50px;
  padding-left: 50px;

  @media (max-width: $media-query1) {
    padding-right: 20px;
    padding-left: 20px;
    width: 100%;
    align-items: center;
  }
  @media (max-width: $media-query1) {
  }
}

//Each project

.project-container {
  display: flex;
  @extend %margin-tiles;

  @media (max-width: $media-query1) {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: fit-content;
  }
}

.project1 .project-container {
  margin-top: 0;
}

//Image-slideshow

.image-container {
  width: 70%;
  min-height: 500px;
  margin-right: 40px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position:relative;

  @media (max-width: $media-query1) {
    height: 100%;
    margin-right: 0;
    width: 100vw;
    flex-direction: column;
    justify-content: flex-start;
  }
}

//Images

.project-image{
  background-position:center;
  background-size: contain;
  background-repeat: no-repeat;
  height:100%;
  width:100%;
  transition: 1000ms steps(7,end);

  @media (max-width: $media-query1) {
  width:90vw;
  height:300px;
  }
}

.project1 .project-image{
  background-image: url("../assets/images/1/1.png");
}

.project2 .project-image{
  background-image: url("../assets/images/2/1.png");
}

.project3 .project-image{
  background-image: url("../assets/images/3/1.png");
}

.project4 .project-image{
  background-image: url("../assets/images/4/1.png");
}

// Arrows

.arrow-wrapper .iconify {
  background-image: url("../assets/svg/Arrow.svg");
  background-repeat: no-repeat;
  background-position: center;
  margin: 5px;
  height: 25px;
  width: 25px;
  border: 5px solid transparent;
}

.arrow {
  background-color: $color-background;
  border: $border-box;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.5s;
}

.arrow:hover{
  background-color: $color-secondary-light;
  transition: 0.5s;
}

.arrow-wrapper {
  width: 40px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;

  @media (max-width: $media-query1) {
    height: 40px;
    margin-right: 0;
    width: 100%;
    margin-bottom: 10px;
    margin-top: 10px;
  }
}

.arrow-previous {
  transform: scaleX(-1);
}

//Slider

.slider{
  margin: 0 auto;
  display:flex;
  position:absolute;
  bottom: -30px;
  left: 50%;
  transform: translate(-50%, -50%);
}

.dot{
  height:10px;
  width:10px;
  margin: 10px 5px 0px 5px;
  background-color: $color-background;
  border:1px solid $color-secondary-dark;
}

.slide-active{
  background-color: $color-secondary-light;
}

/* Project- text */

.project-container .description-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  width: 30%;

  @media (max-width: $media-query1) {
    width: 100%;
    align-items: center;
  }

  @media (max-width: $media-query2) {
    width: 100%;
    margin-top: 40px;
  }
}

.project-container .description-container .txt-container {
  display: flex;
  flex-direction: column;
}

.txt-container h3 {
  margin-bottom: 10px;
  text-shadow: $text-shadow;
  @media (max-width: $media-query1) {
    margin: 0 auto;
    margin-bottom: 20px;
    margin-top: 35px;
    text-align: center;
  }
}

p.project-description {
  margin-bottom: 20px;
  @media (max-width: $media-query1) {
    max-width: 100%;
  }
  @media (max-width: $media-query2) {
  }
}

.roles {
  display: flex;
  flex-wrap: wrap;
}

.roles > :last-child {
  margin-right: 0px;
}

.role {
  @extend %font-small;
  color:$color-secondary-dark;
  border: 2px solid $color-secondary-light;
  margin-bottom: 10px;
  padding: 0px 15px;
  height: 35px;
  width: fit-content;
  margin-right: 10px;
  display: flex;
  align-items: center;
}

//Link arrow

.link-toproject {
  margin-top: 10px;
  width: 100%;
  border: $border-box;
  height: 45px;
  padding: 0px 20px;
  background-color: $color-background;
  align-self: flex-start;
  display: flex;
  align-items: center;
  display: flex;
  align-items: center;
  justify-content: space-between;
  @extend %font-p;
  position: relative;
  transform: translateZ(0);
  transition: all 1200ms;

  @media (max-width: $media-query1) {
    margin-top: 20px;
    max-width: 400px;
    align-self: center;
  }
}

.link-toproject::before{
    content: "";
    position: absolute;
    z-index: -5;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: $color-secondary-light;
    transform: scaleX(0);
    transform-origin: 0 50%;
    transition-property: transform;
    transition: 700ms ease-in;
}

.link-toproject:hover::before {
  transform: scaleX(1);
}

.link-toproject p {
  white-space: nowrap;
  @extend %font-pbold;
  text-align: center;
}

.link-toproject .iconify {
  background-image: url("../assets/svg/Arrow.svg");
  background-repeat: no-repeat;
  background-position: center;
  height: 30px;
  width: 15px;
}

